search.script:
    function gentoday(q)
    {
      if( nullArgs("gentoday", q) )
        return false;

      var links = genealogy_get_known_page_links("http://genealogytoday.com/genealogy/states/", "(.*?) State Genealogy$");
      var search_place_num = genealogy_lookup_place(q);
      if (search_place_num >= 0) {
        openSearchWindow(links[search_place_num]);
      } else {
	openSearchWindow("http://genealogytoday.com/genealogy/states/");
      }
    }
