        openSearchWindow("http://143.207.5.3:82/search/b");
	return;
      } else {
        //openSearchWindow("http://143.207.5.3:82/search/b/"+q);
      }
      document.sdaoif["SEARCH"].value = q;
      submitForm(sdaoif); 
    }
