search.script:
function txdi(q)
{
  if( nullArgs("txdi", q) )
    return;

  if( q == "" )
  {
    openSearchWindow("http://vitals.rootsweb.com/tx/death/search.cgi");
    return;
  }

  // FORM variables for txdif
  document.txdif.surname.value = q;
  //document.txdif.stype.value = "";
  //document.txdif.given.value = "";
  //document.txdif.county.value = "";
  //document.txdif.year.value = "";
  //document.txdif.sex.value = "";

  submitForm(txdif);
}
