search.script:
function ascii(c)
{
  if( nullArgs("ascii",c) )
    return false;
  var char = c.substring(0,1);
  var dec  = char.charCodeAt(0);
  document.deff.q.value = "char: "+char +
    "  dec: "+dec+
    "  oct: "+dec.toString(8)+
    "  hex: "+dec.toString(16)+
    "  bin: "+dec.toString(2);
}
