search.script:
function bg(q)
{
  if( nullArgs("bg", q) )
    return;

  var args = parseArgs(q, "NIV, NASB, AMP, NLT, KJV, ESV, NKJV, KJ21, ASV, WE, YLT, DARBY");
  if ( args.switches.length > 0 )
  {
    document.bgf.version.value = args.switches[0].name.toUpperCase();
  }
  document.bgf.passage.value = args.q;
  submitForm(bgf);
}
