search.script:
function cddb(q, switches)
{
  if( q == "" )
    openSearchWindow("http://www.cddb.com/");
  else if( switches.length > 0 )
  {
    var args = "";
    for( var i = 0; i < switches.length; i++ )
    {
      switch( switches[i] )
      {
        case "artist": args += "artist"; break;
        case "album": args += "disc"; break;
        case "song": args += "track"; break;
      }
      args += (args ? " " : "");
    }
    document.cddbf.q.value = q;
    document.cddbf.f.value = args;
    submitForm(cddbf);
  }
  else
  {
    document.cddbf.q.value = q;
    document.cddbf.f.value = "artist";
    submitForm(cddbf);
  }
}
