search.script:
function cdnow(q, switches)
{
  if( q == "" )
    openSearchWindow("http://www.cdnow.com/");
  else if( switches.length == 1 )
  {
    switch( switches[0] )
    {
      case "album": document.cdnowf.index.value = "t"; break;
      case "song": document.cdnowf.index.value = "s"; break;
      case "label": document.cdnowf.index.value = "l"; break;
      case "video": document.cdnowf.index.value = "m"; break;
      case "actor":
      case "director": document.cdnowf.index.value = "r"; break;
    }
    document.cdnowf.string.value = q;
    submitForm(cdnowf);
  }
  else
  {
    document.cdnowf.index.value = "a";
    document.cdnowf.string.value = q;
    submitForm(cdnowf);
  }
}
