search.script:
// cdnow
function cdnow(q)
{
  var res = q.match(/([^\/]+)(\/[AaSsTtLl])?/);

  if (!res)
  {
     openSearchWindow("http://www.cdnow.com/");
  }
  else
  {
    document.cdnf.string.value=(res[1] ? res[1] : "");
    document.cdnf.index.value=(res[2] ? res[2].toLowerCase().substring(1,2) : "a");
    submitForm(cdnf);
  }
}
