               var imgurl = urlstart+urlcat+urldir+rx.exec(sBody)[1];

               if (imgtitle==null) {
                  //regex for finding the image title in the page source..
                  var tx = /<title>(.+)<\/title>/i;
                  var imgtitle = tx.exec(sBody)[1];
               }
