            var comxSearchPopupBodyCode = "<html><head></head><body><style>" + convertStylesToInline() + "img {vertical-align:top;text-align:left;}</style>";
            comxSearchPopupBodyCode += "<img src=" +imgurl +" width="+windowW+" height="+windowH+" border=0 alt=\""+imgtitle+"\" onclick='self.close()'; galleryimg='no' />";
            comxSearchPopupBodyCode += "</body></html>";
            var top=((screen.availheight-windowH)-38);
            var left=((screen.width-windowW)-10);
            popupImage = window.open('','_blank','toolbar=0,status=0,titlebar=0,location=0,directories=0,menuBar=0,scrollbars=auto,resizable=1,left='+left+',top='+top);
            //popupImage = window.open('','_blank','width='+windowW+',height='+windowH+',toolbar=0,status=0,titlebar=0,location=0,directories=0,menuBar=0,scrollbars=auto,resizable=1,left='+left+',top='+top);
            popupImage.document.open();
            popupImage.document.write(comxSearchPopupBodyCode);
            popupImage.document.close()
            popupImage.document.title=imgtitle;
