            // but we have to expand the window a little to account for borders,
            // title bar, and such.
            // These numbers where chosen empirically, so they may not look correct
            // on other desktops
            windowW = Number(windowW) + 9;
            windowH = Number(windowH) + 34;
            popupImage.resizeTo(windowW, windowH);
         }//if imgurl
      }// for i <...
   }else{
         setSearchWindowText("Bad Comic Name..  try 'comx ?' to see a list");
         setTimeout("setSearchWindowText('comx ')", 2000);
         return;
   }//else
} //function
