search.script:
function conv(q)
{
  if( q == "" )
    openSearchWindow("http://www.convertit.com/Go/Entisoft/Measurement/Converter.ASP");
  else 
  {
    parsedunits = q.split(/ *to */);
    document.convf.From.value=parsedunits[0];
    if (parsedunits[1] != undefined)
      document.convf.To.value=parsedunits[1];
    submitForm(convf);
  }
}
