search.script:
function ebay(q)
{
  if (q == "")
  {
    openSearchWindow("http://www.ebay.com/");
  }
  else if (Number(q))
  {
    if ((Number(q) > '99999999') && (Number(q) < '2147483647'))
    {
      openSearchWindow('http://cgi.ebay.com/aw-cgi/eBayISAPI.dll?MfcISAPICommand=ViewItem&item=' + q);
    }
  }
  else
  {
    document.ebyf.query.value=q;
    submitForm(ebyf);
  }
}
