search.script:
function fe(q)
{
  if( q == "" )
  {
    openSearchWindow("http://www.filext.com/");
  }
  else
  {
    q = q.toLowerCase();
    if ( /^[a-z]$/i.test(q) || ( q == 'sym' ) || ( q == 'num' ) )
      document.filextf.action = "http://www.filext.com/" + q + ".htm";
    else
      document.filextf.action = "http://www.filext.com/";
      submitForm(filextf);
  }
}
