search.script:
function fv(q)
{
  if( nullArgs("fv", q) )
    return false;

  vars = q.split(" ")
  if (vars.length == 2)
  {
    document.fvf.aln.value = vars[0].toUpperCase();
    document.fvf.fn.value = vars[1];
    document.fvf.ddate.value = "Today";
    submitForm(fvf);
  }
  else
    nullArgs("fv","?");
}
