search.script:
function gg(q, switches)
{
  if( q == "" )
  {
    switch( switches[0] )
    {
      case "images": openSearchWindow("http://images.google.com/"); break;
      case "directory": openSearchWindow("http://images.google.com/dirhp?hl=en"); break;
      default: openSearchWindow("http://www.google.com/"); break;
    }
  }
  else if( switches.length == 1 )
    switch( switches[0] )
    {
      case "directory":
        document.ggf.q.value = q;
        document.ggf.cat.value = "gwd/Top";
        submitForm(ggf);
        break;
      case "images":
        document.imagef.q.value = q;
        submitForm(imagef);
        break;
      case "related":
      case "similar":
        document.ggf.q.value="related:" + q;
        submitForm(ggf);
        break;
      case "cache":
        document.ggf.q.value="cache:" + q;
        submitForm(ggf);
        break;
      case "link":
        document.ggf.q.value="link:" + q;
        submitForm(ggf);
        break;
    }
  else
  {
    document.ggf.q.value = q;
    submitForm(ggf);
  }
}
