search.script:
function ggd(q){
  if( nullArgs( "ggd", q ) ){
    return false;
  }
  var args = parseArgs(q, "about");
  for (var i = 0; i < args.switches.length; i++){
    sw = args.switches[i];
    switch(sw.name){
      case "about":
        openSearchWindow("http://desktop.google.com/");
        return;
    }
  }
  document.ggdf.q.value = args.q;
  submitForm(ggdf);
}
