search.script:
    function ggd(q){
	if( nullArgs( "ggd", q ) ){
	  return false;
	}
	if(typeof ggd_googleKey == "undefined"){
	  openSearchWindow("http://reliableanswers.com/x/dqsd/ggd-discover.asp");
	}else{
	  document.ggdf.action  = "http://127.0.0.1:4664/search&s=" + ggd_googleKey;
	  document.ggdf.q.value = q;
	  submitForm(ggdf);
	}
    }
