    function gt(q)
    {
      if( nullArgs("gt", q) )
        return;

      var args = parseArgs(q, "songs, bands, news, reviews, lessons, promos, users");
      if ( args.switches.length > 0 )
        document.gtf.w.value = args.switches[0].name;
      else
        document.gtf.w.value = "songs";

      document.gtf.s.value = args.q;

      submitForm(gtf);
    }
  
  
