search.script:
function hs(q)
{
  var args = parseArgs(q, "asp, c, java, javascript, php, perl, python, remotehosts, tools, xml");

  if( nullArgs("hs",q) )
    return false;
  else if( args.switches.length == 1 )
  {
    switch( args.switches[0].name )
    {
      case "asp": document.hsf.category.value = "ASP"; break;
      case "c": document.hsf.category.value = "C"; break;
      case "java": document.hsf.category.value = "Java"; break;
      case "javascript": document.hsf.category.value = "JavaScript"; break;
      case "php": document.hsf.category.value = "PHP"; break;
      case "perl": document.hsf.category.value = "Perl"; break;
      case "python": document.hsf.category.value = "Python"; break;
      case "remotehosts": document.hsf.category.value = "Remote_Hosts"; break;
      case "tools":
      case "utils": document.hsf.category.value = "Tools"; break;
      case "xml": document.hsf.category.value = "XML"; break;
    }
    document.hsf.query.value = args.q;
    submitForm(hsf);
  }
  else if( args.switches.length > 1 )
    nullArgs("hs","?");
  else
  {
    document.hsf.category.value = "all";
    document.hsf.query.value = args.q;
    submitForm(hsf);
  }
}
