search.script:
function mysimon(q)
{
  if( nullArgs("mysimon", q) )
    return;

  var args = parseArgs(q, "apparel, babies, beauty, health, books, movies, music, computers, electronics, flowers, gifts, food, wine, home, garden, office, sports, hobbies, toys, games");
  var c = "";
  if ( args.switches.length > 0 )
  {
    switch( args.switches[0].name )
    {
      case "apparel":
        c = "apparel"; break;
      case "babies":
        c = "babieskids"; break;
      case "beauty":
        c = "health"; break;
      case "health":
        c = "health"; break;
      case "books":
        c = "media"; break;
      case "movies":
        c = "media"; break;
      case "music":
        c = "media"; break;
      case "computers":
        c = "computers"; break;
      case "electronics":
        c = "electronics"; break;
      case "flowers":
        c = "flowersgifts"; break;
      case "gifts":
        c = "flowersgifts"; break;
      case "food":
        c = "gourmet"; break;
      case "wine":
        c = "gourmet"; break;
      case "home":
        c = "home"; break;
      case "garden":
        c = "home"; break;
      case "office":
        c = "office"; break;
      case "sports":
        c = "sportshobbies"; break;
      case "hobbies":
        c = "sportshobbies"; break;
      case "toys":
        c = "toyscollectibles"; break;
      case "games":
        c = "computerandvideogames"; break;
      default:
        break;
    }
  }

  document.mysimonf.kw.value = args.q;
  document.mysimonf.c.value = c;

  submitForm(mysimonf);
}
