search.script:
    var adddate=true;

    function getNoteFile() {
		return DQSDLauncher.AppDataDirectory + "\\note.txt";
	}

    function addNote(info) {
		try {
			appendFile(getNoteFile(), info+"\r\n");
			setSearchWindowText("");
		}
		catch(e) { }
    }

    function clearNote() {
		try {
			writeFile(getNoteFile(), "");
			setSearchWindowText("");
		}
		catch(e) { }
	}

	function displayNotes()	{
		openSearchWindow(getNoteFile());
	}

    function note(info) {
	    var cleared = false;
		var display = false;
		var args = parseArgs(info, "clear, display");
        for (var i = 0; i < args.switches.length; i++) {
			sw = args.switches[i];
			switch(sw.name) {
			case 'clear': clearNote(); cleared = true; break;
			case 'display': display = true; break;
			default: break;
			}
		}

		info = args.q;
		if (info == "?") {
			nullArgs("note", info);
		} else if (info == "") {
		    if (!cleared) {
				display = false;
				displayNotes();
			}
		} else if (info == "clear") {
			clearNote();
		} else {
			if (adddate) {
				 var now = new Date();
				 info = formatdate(now, clockformat) + "-   " +  info;
			}
			addNote(info);	
	    }
		if (display) {
			displayNotes();
		}
    }
