search.script:
function temp(q) 
{
  var args = parseArgs(q, "celsius, fahrenheit");

  if( nullArgs("temp",q) )
    return false;
  else if( args.switches.length == 1 )
  {
    switch( args.switches[0].name )
    {
      case "celsius":     // convert a celsius value to fahrenheit
        document.deff.q.value = args.q + "\u00b0C = " + parseInt(eval((1.8 * args.q) + 32),10) + "\u00b0F";
        document.deff.q.createTextRange().select();
        break;

      case "fahrenheit":  // convert a fahrenheit value to celsius
        document.deff.q.value = args.q + "\u00b0F = " + parseInt(eval((args.q - 32) / 1.8),10) + "\u00b0C";
        document.deff.q.createTextRange().select();
        break;
    }
  }
  else
    nullArgs("temp","?");
}
