search.script:
function thw(q)
{
  if( nullArgs("thw",q) )
    return false;
  else
  {
    var args = parseArgs(q, "category");
    var category = args.switch_val["category"];
    if( typeof category == "undefined" ) category = "all";
    switch( category )
    {
      case "all": break;
      case "mainboard": break;
      case "cpu": break;
      case "graphic": break;
      case "display": break;
      case "video": break;
      case "storage": break;
      case "network": break;
      case "mobile": break;
      case "consumer": break;
      case "howto": break;
      case "business": break;
      default: nullArgs("thw","?"); return false;
    }
    document.thwf.words.value = args.q;
    document.thwf.category.value = category;
    submitForm(thwf);
  }
}
