search.script:
function unip(q)
{
  var suffix;
  var result;

  if (q == "")
    openSearchWindow("http://www.uniplus.ch/cgi-bin/dig.cgi");
  else if( switches.length == 1 )
  {
    if( switches[0] == "mx" )
    {
      document.digdnsf.domain.value = q;
      document.digdnsf.type.value = "MX";
      submitForm(digdnsf);
    }
  }
  else
  {
    document.digdnsf.domain.value = q;
    document.digdnsf.type.value = "ANY";
    submitForm(digdnsf);
  }
}
