search.script:
function ups(q)
{
  if( nullArgs("ups", q) )
    return false;

  if (q == "")
    openSearchWindow("http://www.ups.com/tracking/tracking.html");
  else
  {
    if (result = q.split( /\s*,+\s*/ ) )
    {
      for ( var i = 1; i <= 20; i++ ) {
        if (i <= result.length) {
          track = result[i - 1];
        } else {
          track = "";
        }
        track = track.replace(/\W/g,"");
        eval( "document.upsf.InquiryNumber" + i + ".value='" + track + "';" );
      }

      document.upsf.tracknums_displayed.value = result.length;
      submitForm(upsf);
    }
  }
}
