search.script:
function zc(q)
{
  if (q == "")
    window.open("http://www.zipinfo.com");
  else
  {
    if (result = q.match(/^(\d{5})\s*(.*)/))
      document.zipcode.zip.value = result[1];
    else if (result = q.match(/^(.*)/))
      document.zipcode.zip.value = result[1];

    submitForm(zipcode);
  }
}
