search.script:
function everton(q)
{
  if( nullArgs("everton", q) )
    return;

  var surname = genealogy_parse_surname(q);
  var givenname = genealogy_parse_givenname(q);
  genealogy_openSearchWindow("http://www.everton.com/search/simple.php?submit=Search"+
           "&amp;surname="+surname+"&amp;given_name="+givenname, "everton", q);
}
