search.script:
    function everton(q)
    {
      if( nullArgs("everton", q) )
        return;

      openSearchWindow("http://www.everton.com/search/simple.php?submit=Search"+
                       "&amp;surname="+genealogy_parse_surname(q)+
		       "&amp;given_name="+genealogy_parse_givenname(q));
    }
