search.script:
    function ildc(q)
    {
      if( nullArgs("ildi", q) )
        return;

      if( q == "" )
      {
        openSearchWindow("http://www.cyberdriveillinois.com/");
        return;
      }
	var args = parseArgs(q, "county");
	if (args != null) {
	        document.ildcf.FirstName.value = "";
	        document.ildcf.MiddleName.value = "";
	        document.ildcf.county.value = "";
	        document.ildcf.more.value = "";
		document.ildcf.county.value = "";
		if (args.switches.length == 1) {
			if (args.switches[0].name == "county") {
				document.ildcf.county.value =  args.switch_val["county"].toUpperCase();
			}
		}
		document.ildcf.LastName.value = genealogy_parse_surname(args.q);
		document.ildcf.FirstName.value = genealogy_parse_givenname(args.q);
		submitForm(ildcf);
		return;
	}
	nullArgs("ildi", "?");
    }
