search.script:
    function ilmi(q)
    {
      if( nullArgs("ilmi", q) )
        return;

      if( q == "" )
      {
        openSearchWindow("http://www.cyberdriveillinois.com/departments/archives/marriage.html");
        return;
      }

      var args = parseArgs(q, "county, bride, groom");
      if (args != null) {
	document.ilmif.groom.value = "";
	document.ilmif.bride.value = "";
	document.ilmif.county.value = "";
	if (args.switches.length > 0) {
	        for (var i=0; i < args.switches.length; i++) {
		  switch (args.switches[i].name)
		  {
			case "groom": document.ilmif.groom.value = args.switch_val["groom"]; break;
			case "bride": document.ilmif.bride.value = args.switch_val["bride"]; break;
			case "county": document.ilmif.county.value = args.switch_val["county"].toUpperCase(); break;
  		  }
		}
	}
	if (document.ilmif.groom.value == "" && document.ilmif.bride.value == "") {
		nullArgs("ilmi", "?");
		return;
	}
        submitForm(ilmif);
      }
    }
