search.script:
    function lds(q)
    {
      if( nullArgs("lds", q) )
        return;

      var args = parseArgs(q, "all, af, igi, ssdi");
      if (args != null && args.switches.length > 0) {
	if (args.switches.length > 1) {
	  nullArgs("lds", "?");
	  return;
	}
	switch (args.switches[0].name)
	{
		case 'af':
			openSearchWindow("http://www.familysearch.org/Eng/Search/frameset_search.asp?PAGE=af/search_AF.asp&clear_form=true");
			return;
		case 'ssdi':
			openSearchWindow("http://www.familysearch.org/Eng/Search/frameset_search.asp?PAGE=ssdi/search_ssdi.asp&clear_form=true");
			return;
		case 'igi':
			openSearchWindow("http://www.familysearch.org/Eng/Search/frameset_search.asp?PAGE=igi/search_IGI.asp&clear_form=true");
			return;
		default:
			openSearchWindow("http://www.familysearch.org/Eng/Search/frameset_search.asp?PAGE=search_all1.asp&clear_form=true");
			return;
        }
      }

      document.ldsf.last_name.value = genealogy_parse_surname(args.q);
      document.ldsf.first_name.value = genealogy_parse_givenname(args.q);
      submitForm(ldsf);
    }
