search.script:
function mimi(q)
{
  if( nullArgs("mimi", q) )
    return;

  if( q == "" )
  {
    openSearchWindow("http://www.mifamilyhistory.org/dibeanindex/dib_marr/search.asp");
    return;
  }

  // FORM variables for mimif
  document.mimif.SURNAME.value = genealogy_parse_surname(q);

  submitForm(mimif);
}
