search.script:
function newsar(q)
{
  if( nullArgs("newsar", q) )
    return;

  var surname = genealogy_parse_surname(q);
  var givenname = genealogy_parse_givenname(q);

  var qry = surname;
  if (givenname.length > 0) {
    qry = givenname+" "+surname;
  }

  document.newsarf.SearchString.value = qry;
  submitForm(newsarf);
}
