search.script:
    function town(q)
    {
      if( nullArgs("town", q) )
        return;

      var parts = q.split(",");
      document.townf.city.value = genealogy_trim(parts[0]);
      if (parts.length > 1) {
        document.townf.state.value = genealogy_lookup_place_abbrev(genealogy_trim(parts[1]));
      } else {
	document.townf.state.value = "";
      }
      submitForm(townf);
    }
