search.script:
    function wsmarr(q)
    {
      if( nullArgs("wsmarr", q) )
        return;

      if( q == "" )
      {
        openSearchWindow("http://abish.byui.edu/specialCollections/fhc/gbsearch.htm");
        return;
      }

      var args = parseArgs(q, "all, bride, groom");
      if (args != null) {
	var do_groom = 1;
	var do_bride = 1;
	if (args.switches.length > 0) {
		do_groom = 0;
		do_bride = 0;
	        for (var i=0; i < args.switches.length; i++) {
		  switch (args.switches[i].name)
		  {
			case "groom": do_groom = 1; break;
			case "bride": do_bride = 1; break;
			case "all":
			default:
			  do_groom = 1; do_bride = 1; break;
  		  }
		}
	}
	if (args.q == "") {
		do_groom = 0;
		do_bride = 0;
	}
	if (!do_groom && !do_bride) {
		nullArgs("wsmarr", "?");
		return;
	}
	if (do_bride) {
		document.wsmarrbf.b_firstname.value = "";
		document.wsmarrbf.b_lastname.value = args.q;
		submitForm(wsmarrbf);
	}
	if (do_groom) {
		document.wsmarrgf.g_firstname.value = "";
		document.wsmarrgf.g_lastname.value = args.q;
		submitForm(wsmarrgf);
	}
      }

    }
