search.script:
function alm(q, switches)
{
  if( q == "" )
     openSearchWindow("http://www.allmusic.com/");
  else if( switches.length == 1 )
  {
    switch( switches[0] )
    {
      case "artist": 
        document.almf.opt1.value="1"; 
        break;
      case "album": 
        document.almf.opt1.value="2"; 
        break;
      case "label":
        document.almf.opt1.value="4"; 
        break;
      case "style":
        document.almf.opt1.value="5"; 
        break;
    }
    document.almf.sql.value = q;
    submitForm(almf);
  }
  else
  {
    document.almf.opt1.value="3";
    document.almf.sql.value = q;
    submitForm(almf);
  }
}
