search.script:
function ascii(c)
{
if (c == "")
{
  return;
}
  var char = c.substring(0,1);
  var dec  = char.charCodeAt(0);
  var oct  = dec.toString(8)
  var hex  = dec.toString(16)
  var bin  = dec.toString(2)
  document.deff.q.value = "char: "+char +"  dec: "+dec+"  oct: "+oct+"  hex: "+hex+"  bin: "+bin;
  //document.deff.q.createTextRange().select();
}
