search.script:
function bfe(q)
{
  if( nullArgs("bfe", q) )
    return false;
  else
  {
    var res = q.match(/(((\w+\s+)?\w+)\s+)?(\w+)(\s*\((([^,]+),?\s+)?(\w\w)\))?/);

    document.bfef.FirstName.value=(res[2] ? res[2] : "");
    document.bfef.LastName.value=(res[4] ? res[4] : "");
    document.bfef.City.value=(res[7] ? res[7] : "");
    document.bfef.State.value=(res[8] ? res[8].toUpperCase() : "");
    submitForm(bfef);
  }
}
