search.script:
function email(q)
{
  if( nullArgs("email",q) )
    return false;

  var action = "mailto:";
  var tokens = q.split( ';' );
  if( tokens[0] )
  {
    action += tokens[0].replace(/,/, ";");
    if (tokens[1])
    {
      var subject = tokens[1].replace( /^\s/, '' ).replace( /\s$/, '' )
      action += ("?subject=" + subject);
      if (tokens[2])
      {
        var body = tokens[2].replace( /^\s/, '' ).replace( /\s$/, '' )
        action += ("&body=" + body);
      }
    }
  }
  window.location.href = action;
}
