search.script:
function gg(q)
{
  var args = parseArgs(q, "advanced, images, directory, related, cache, link, groups, uk, canada, local");
  var mode, local, locale_meta = "", locale_tld = "com";

  // Establish the locale and mode the search will run in.
  if( args.switches.length > 0 )
    for( var j=0; j<args.switches.length; j++ )
      switch( args.switches[j].name )
      {
        case "uk": locale_tld = "co.uk"; locale_meta = "cr=countryUK|countryGB"; break;
        case "canada": locale_tld = "ca"; locale_meta = "cr=countryCA"; break;

        case "local": local = true; break;

        default:
          if(mode) { 
            nullArgs("gg","?"); return false; } 
          else
            mode = args.switches[j].name;
          break;
      }

  // Change the form data to use the correct locale
  document.ggf.action = "http://www.google." + locale_tld + "/search";
  document.ggf_adv.action = "http://www.google." + locale_tld + "/advanced_search";
  document.ggf_image.action = "http://images.google." + locale_tld + "/images";
  document.ggf_news.action = "http://groups.google." + locale_tld + "/groups";

  // If the user specified to search localized content only, restrict it now.
  if(local)
  {
    document.ggf.meta.value = locale_meta;
    document.ggf_image.meta.value = locale_meta;
    document.ggf_news.meta.value = locale_meta;
  }
  else
    document.ggf.meta.value = document.ggf_image.meta.value = document.ggf_news.meta.value = "";

  // No parameters, so go to the appropriate web page.
  if( args.q == "" )
    switch( mode )
    {
      case "advanced": openSearchWindow("http://www.google." + locale_tld + "/advanced_search"); return;
      case "images": openSearchWindow("http://images.google." + locale_tld); return;
      case "directory": openSearchWindow("http://images.google." + locale_tld + "/dirhp"); return;
      case "groups": openSearchWindow("http://www.google." + locale_tld + "/grphp"); return;
      default: openSearchWindow("http://www.google." + locale_tld); return;
    }

  // Show the help pop-up
  else if( args.q == "?" )
    nullArgs("gg","?");

  else if( mode )
  {
    // Perform a validity check on the specified switch
    if((mode == "related" || mode == "cache" || mode == "link") && !isURL(args.q)) {
      nullArgs("gg","?"); return false; }

    switch( mode )
    {
      case "advanced":
        document.ggf_adv.q.value = args.q;
        submitForm(ggf_adv);
        break;
      case "directory":
        document.ggf.q.value = args.q;
        document.ggf.cat.value = "gwd/Top";
        submitForm(ggf);
        break;
      case "images":
        document.ggf_image.q.value = args.q;
        submitForm(ggf_image);
        break;
      case "related":
        document.ggf.q.value="related:" + args.q;
        submitForm(ggf);
        break;
      case "cache":
        document.ggf.q.value="cache:" + args.q;
        submitForm(ggf);
        break;
      case "link":
        document.ggf.q.value="link:" + args.q;
        submitForm(ggf);
        break;
      case "groups":
        document.ggf_news.q.value = args.q;
        submitForm(ggf_news);
        break;
    }
  }
  else
  {
    document.ggf.q.value = args.q;
    submitForm(ggf);
  }
}
