search.script:
function gg(q, switches)
{
  if (q == "")
    openSearchWindow("http://www.google.com/");
  else if( switches.length == 1 )
  {
    switch( switches[0] )
    {
      case "images":
        document.imagef.q.value = q;
        submitForm(imagef);
        break;
      case "related":
      case "similar":
        document.ggf.q.value="related:" + q;
        submitForm(ggf);
        break;
      case "cache":
        document.ggf.q.value="cache:" + q;
        submitForm(ggf);
        break;
      case "link":
        document.ggf.q.value="link:" + q;
        submitForm(ggf);
        break;
    }
  }   
  else
  {
    document.ggf.q.value = q;
    submitForm(ggf);
  }
}
