search.script:
// Uses Google I'm feeling lucky search
function jdk(q)
{
  // Note: Google does not index 1.2 for some reason.
  var args = parseArgs(q, "1.4, 1.3");
  if (q == "")
    openSearchWindow("http://java.sun.com/j2se/1.4/docs/api/");
  else if (args.switches.length > 1)
    nullArgs("jdk","?");
  else
  {
    var version = "v1.4.0";
    if (args.switches.length == 1)
    {
      switch (args.switches[0].name)
      {
        case "1.3":
          version = "v1.3.1"; break;
        case "1.4":
        default:
          version = "v1.4.0"; break;
      }
      if (args.q == "")
      {
        openSearchWindow("http://java.sun.com/j2se/" + args.switches[0].name + "/docs/api/");
        return;
      }
    }
    document.jdkf.q.value='intitle:' + args.q + ' "' + version + '" site:java.sun.com inurl:docs/api';
    submitForm(jdkf);
  }
}
