search.script:
    var adddate=true;

    function addNote(info) {
     try {
       appendFile("note.txt", info+"\r\n");
     }
     catch(e) { }
    }

    function clearNote() {
     try {
       writeFile("note.txt", "");
     }
     catch(e) { }
    }

    function note(info) {
        if (info == "")
        {
                var path = DQSDLauncher.InstallationDirectory;
                path += "\\note.txt";
                openSearchWindow(path);
        }

     else {
      if (info == "clear") clearNote();
      else{
	if (adddate) {
         var now = new Date();
         info = formatdate(now, clockformat) + "-   " +  info;
	}
	addNote(info);	
      }
      document.deff.q.value = "";
     }
    }
