search.script:
function pw(q) {
  if (q == "")
    openSearchWindow("http://www.pricewatch.com");
  else {
    /* mb - Motherboards... cpu - Microprocessors... ram - System Memory... hd - Hard Disk Drives...cd - CD-ROM Drives...  vc - Video Cards... sc - Sound Cards... cdrw - CD Recorders...
    dvd - DVD Drives... fd - Floppy Drives... mon - Monitors... case - Cases...  ps - Power Supply for Case... bb - Computer Systems - Kits Bare Bones...*/
    if (res = q.match(/^(\w*):(.*)/)) {
      switch(res[1]) {
        case 'mb':
          pw_q = "Motherboards "+res[2];break;
        case 'cpu':
          pw_q = "Microprocessors "+res[2];break;
        case 'ram':
          pw_q = "System Memory "+res[2];break;
        case 'hd':
          pw_q = "Hard Disk Drives "+res[2];break;
        case 'cd':
          pw_q = "CD-ROM Drives "+res[2];break;
        case 'v':
          pw_q = "Video Cards "+res[2];break;
        case 's':
          pw_q = "Sound Cards "+res[2];break;
        case 'cdrw':
          pw_q = "CD Recorders "+res[2];break;
        case 'dvd':
          pw_q = "DVD Drives "+res[2];break;
        case 'fd':
          pw_q = "Floppy Drives "+res[2];break;
        case 'mon':
          pw_q = "Monitors "+res[2];break;
        case 'case':
          pw_q = "Cases "+res[2];break;
        case 'ps':
          pw_q = "Power Supply for Case "+res[2];break;
        case 'bb':
          pw_q = "Computer Systems - Kits Bare Bones "+res[2];break;
        default:
          pw_q = res[2];
      } // end switch
    } else {
      // no reg exp match
      pw_q = q;
    }
    openSearchWindow("http://brook.pricewatch.com/search/search.asp?criteria="+escape(pw_q));
  }
}
