search.script:
    function qac(q){

      if( nullArgs("qac", q) )
        return false;
      else{
	if (result = q.match(/^\(?(\d{3})\)?\s*[\-\.]?\s*(\d{3})(\s*[\-\.]?\s*\d{4}\s*)?$/)){
	  document.qacf.npa.value=result[1];
	  document.qacf.nxx.value=result[2];
	}
        submitForm(qacf);
      }

    }
