search.script:
function sb(q)
{
  res = q.match(/(((\w+\s+)?\w+)\s+)?(\w+)(\s*\((([^,]+),?\s*)?(\w\w)\))?/);
  if (!res)
    openSearchWindow("http://www.switchboard.com/bin/cgiqa.dll");
  else
  {
    document.sbf.F.value=(res[2] ? res[2] : "");
    document.sbf.L.value=(res[4] ? res[4] : "");
    document.sbf.T.value=(res[7] ? res[7] : "");
    document.sbf.s.value=(res[8] ? res[8].toUpperCase() : "");
    submitForm(sbf);
  }
}
