search.script:
function temp(q) 
{
  var args = parseArgs(q, "celsius, centigrade, fahrenheit, kelvin, rankine");

  if( nullArgs("temp",q) )
    return false;
  else if( args.switches.length == 1 )
  {
    switch( args.switches[0].name )
    {
      case "celsius": // convert a Celsius value.
      case "centigrade":
        document.deff.q.value = args.q + "\u00b0C = " + parseInt(eval(1.8 * args.q + 32 + 0.5),10) + "\u00b0F " + parseInt(eval(1 * args.q + 273.15 + 0.5),10) + "\u00b0K " + parseInt(eval((1.8 * args.q) + 32 + 459.67 + 0.5),10) + "\u00b0R";
        document.deff.q.createTextRange().select();
        break;

      case "fahrenheit": // convert a Fahrenheit value
        document.deff.q.value = args.q + "\u00b0F = " + parseInt(eval((args.q - 32) / 1.8 + 0.5),10) + "\u00b0C " + parseInt(eval((args.q - 32) / 1.8 + 273.15 + 0.5),10) + "\u00b0K " + parseInt(eval(1 * args.q + 459.67 + 0.5),10) + "\u00b0R";
        document.deff.q.createTextRange().select();
        break;

      case "kelvin": // convert a Kelvin value
        document.deff.q.value = args.q + "\u00b0K = " + parseInt(eval(1 * args.q - 273.15 + 0.5),10) + "\u00b0C " + parseInt(eval((1.8 * args.q) - 459.67 + 0.5),10) + "\u00b0F " + parseInt(eval(1.8 * args.q + 0.5),10) + "\u00b0R";
        document.deff.q.createTextRange().select();
        break;

      case "rankine": // convert a Rankine value
        document.deff.q.value = args.q + "\u00b0R = " + parseInt(eval((args.q) / 1.8 - 273.15 + 0.5),10) + "\u00b0C " + parseInt(eval((args.q) * 1 - 459.67 + 0.5),10) + "\u00b0F " + parseInt(eval((args.q) / 1.8 + 0.5),10) + "\u00b0K";
        document.deff.q.createTextRange().select();
        break;
    }
  }
  else
    nullArgs("temp","?");
}
