search.script:
function ubs(q)
{
  if( nullArgs("ubs", q) )
    return;

  var args = parseArgs(q, "author, title, keywords");
  if ( args.switches.length > 0 )
  {
    switch( args.switches[0].name )
    {
      case "author":
        document.ubsf.author.value = q;
        break;
      case "title":
        document.ubsf.title.value = q;
        break;
      case "keywords":
        document.ubsf.keywords.value = q;
        break;
      default:
        document.ubsf.keywords.value = q;
        break;
    }
  }

  else
  {
  document.ubsf.keywords.value = q;
  }

  submitForm(ubsf);
}
