search.script:
function unip(q)
{
  var suffix;
  var result;

  if (q == "")
  {
    openSearchWindow("http://www.uniplus.ch/cgi-bin/dig.cgi");
  }
  else
  {
    // Check for / suffix, such as unip yahoo.com /MX
    if (result = q.match(/^(.*)\/(\w+)/))
    {
      suffix = result[2];
      q = result[1];
    }
    else
    {
      suffix = "ANY";
    }

    document.digdnsf.domain.value=q;
    document.digdnsf.type.value=suffix;
    submitForm(digdnsf);
  }
}
